script.code:
String name = '${occi.compute.hostname}'
String ifconfig = "ifconfig eth0".execute().text.trim() String hostname = "hostname".execute().text.trim()
println "IFCONFIG COMMAND: '$ifconfig'" println "HOSTNAME COMMAND: '$hostname'"
def regex = /(?s).*inet addr:(.*?)Bcast:.*/ def matcher = (ifconfig=~regex)
def ip = "unknownip" if (matcher.matches()) { ip = matcher.group(1).trim() } else { println("Cannot extract IP") }
def json = new net.minidev.json.JSONObject(); json.put("occi.networkinterface.address", ip.trim()) json.put("occi.compute.hostname", name.trim()) json.put("occi.compute.organization.name", hostname.trim()) json.put("action.state", "done") json.put("occi.compute.state", "up") json.put("occi.paas.application.endpoints", "http://" + ip.trim() + ":9200") result = json.toJSONString() println result
